mapper.select:
select u.user_id, u.dept_id, u.client_id, u.login_name, u.user_name, u.email, u.avatar, u.phone_number, u.password, u.sex, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
and u.client_id = #{clientId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.status = #{status}
AND u.phone_number like concat('%', #{phoneNumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.email, u.avatar, u.phone_number, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phone_number like concat('%', #{phoneNumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, client_id = #{clientId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phone_number = #{phoneNumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
